import { observable, action, computed, makeObservable } from "mobx";

class TodoStore {
  constructor() {
    makeObservable(this);
  }

  @observable
  _todo = [
    { id: 1, task: "Study", completed: false },
    { id: 2, task: "Reading", completed: true },
  ]; // id, title, date

  get todo() {
    return this._todo;
  }
}

export default new TodoStore();
