import { observable, action, computed, makeObservable, toJS } from "mobx";

class TodoStore {
  constructor() {
    makeObservable(this);
  }

  @observable
  _todo = {}; // id, title, date

  @observable
  _todos = [];

  get todo() {
    return this._todo;
  }

  @computed
  get todos() {
    return toJS(this._todos);
  }

  @action
  setTodoProps(name, value) {
    this._todo = {
      ...this._todo,
      [name]: value,
    };
  }

  @action
  addTodo(todo) {
    this._todos.push(todo);
  }

  @action
  selectedTodo(todo) {
    this._todo = todo;
  }
}

export default new TodoStore();
